function onUse(cid, item, frompos, item2, topos)
         local b = a[item.itemid]                                    
               if not b then return true end
         local pokemon = b.pokemons[math.random(#b.pokemons)]
		if not pokes[pokemon] then return true end  
         
         doPlayerSendTextMessage(cid, 27, "Você acaba de abrir a Box+"..getItemInfo(item.itemid).name.."!")
		 doPlayerSendTextMessage(cid, 27, "Você ganhou o pokémon "..pokemon..", parabéns!")
		 doSendMagicEffect(getThingPos(cid), 29)
               
         addPokeToPlayer(cid, pokemon, 0, nil, "poke")     --alterado v1.9                                                 
         doRemoveItem(item.uid, 1)
    
return true
end
