function onUse(cid, item, fromPosition, itemEx, toPosition)
	
	if getPlayerLevel(cid) < 180 then
		doPlayerSendCancel(cid, "Você precisa de no minimo level 180 para usar o radar!")
		return true
	end
	
	if not getItemAttribute(item.uid, "dono") then
		doItemSetAttribute(item.uid, "dono", getCreatureName(cid))
		return true
	end
	
	if getCreatureName(cid) ~= getItemAttribute(item.uid, "dono") then
		doPlayerSendCancel(cid, "Só o "..getItemAttribute(item.uid, "dono").." poderá usar esse radar.")
		return true
	end		
	
	if getPlayerStorageValue(cid, 5465400) - os.time() > 0 then
		doPlayerSendCancel(cid, "Aguarde "..convertTime(getPlayerStorageValue(cid, 5465400) - os.time()).." para você usar novamente o radar.")
		return true
	end
	
	doSummonBoss(cid)
	setPlayerStorageValue(cid, 5465400, 30 * 60 + os.time())
	return true  
end
